package week1.day2;

public class OddOrEven {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int input=7;
		if(input%2==0)
		{
			System.out.println("The input "+input+" is even");
			
		}
		else
		{
			System.out.println("The input "+input+" is odd");
			
		}
	}

}
