package week1.day2;

public class PositiveOrNegative {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int number = 34;
		if(number<0)
		{
			System.out.println("The number "+number +" is negative");
		}
		else if(number>0)
		{
			System.out.println("The number "+number +" is positive");
		}
		
	}

}



